#!/bin/bash

http_code=200
mime=text/html

echo -e "HTTP/1.1 $http_code OK\r"
echo "Content-type: $mime"
echo
echo "Set-Cookie: name=cookie-time; Path=/; HttpOnly; Content="@START@@END@"; Expires=Wed, 30 Aug 2019 00:00:00 GMT"

echo "<html><body><p>"
printf '%s\n' "$HTTP_COOKIE"
echo "</p></body></html>"
